html, body {
    height: 100%;
    width: 100%;
    background: #4A7FB1;
    display: block;
    margin: 0;
    padding: 0;
}

#display {
    color: white;
    font-family: Helvetica, sans-serif;
    font-size: 30px;
    position: absolute;
    transform: translate(-50%, -50%);
    font-weight: bold;
}
