css:
html, body {
    height: 100%;
    width: 100%;
    background: #4A7FB1;
    display: block;
    margin: 0;
    padding: 0;
}

#display {
    color: white;
    font-family: Helvetica, sans-serif;
    font-size: 30px;
    position: absolute;
    transform: translate(-50%, -50%);
    font-weight: bold;
}

js:
var el = document.getElementById("display");

var lastMS = 0;
function update (ms) {
    lastMS = ms;
    var start = -0.5 * Math.PI,
        position = 2 * Math.PI * ( ms % 5000 ) / 5000,
        radius = 125;
    el.style.left = radius * Math.cos(start + position) + 0.5 * window.innerWidth + "px";
    el.style.top = radius * Math.sin(start + position) + 0.5 * window.innerHeight + "px";
}

function animate (fn) {
    window.requestAnimationFrame(function (ms) {
        fn(ms);
        animate(fn);
    });
}

animate(update);

window.addEventListener("resize", function () {
    update(lastMS);
});
